import React, { useEffect, useState } from 'react';
import api from '../../services/api';
import './styles.css';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';

export default function Produtos({ history }) {
    let [qtdProduto, setQtdProduto] = useState('');
    let produtoFinal = localStorage.getItem('produto');
    produtoFinal = JSON.parse(produtoFinal);
    const [produto, setProduto] = useState([qtdProduto]);

    useEffect(() => {
        async function carregarProduto() {
            const id = localStorage.getItem('produto_id');
            const response = await api.get(`/products/${id}`);
            produtoFinal.price = `R$ ${response.data.price.toLocaleString('pt-BR')}`
            setProduto(produtoFinal)

            /* cart.forEach(car => {
                if (car.id === id)
                    return setQtdProduto(car.quantidade)
            }) */
        }

        carregarProduto();
    }, [qtdProduto]);

    function handleRemove(id) {
        /*localStorage.setItem('qtdTotal', qtdTotal--);
        document.getElementById('idQtd').innerHTML = qtdTotal;*/
    };

    function handleAdd(produto) {
        console.log(produto)
        /*localStorage.setItem('produto', produto.qtd++);
        document.getElementById('idQtd').innerHTML = qtdTotal;*/
    };

    return (
        <>
            <div className="linhaUm">
                <header style={{backgroundImage: `url(${produto.picture})`}} />
                <div>
                    <h2>{produto.title}</h2>
                    <div className="divBtns">
                        <button className="btnRemover" onClick={() => handleRemove(produto.id)}>-</button>
                        <input type="text" value={qtdProduto} readOnly />
                        <button className="btnAdicionar" onClick={() => handleAdd(produto)}>+</button>
                    </div>
                </div>
            </div>
            <Paper>
                <Table>
                    <TableBody>
                        <TableRow>
                            <TableCell className="colunaEsquerda">
                                Valor
                            </TableCell>
                            <TableCell>
                                {produto.price}
                            </TableCell>
                        </TableRow>
                        <TableRow>
                            <TableCell className="colunaEsquerda">
                                Descrição
                            </TableCell>
                            <TableCell>
                                {produto.description}
                            </TableCell>
                        </TableRow>
                        <TableRow>
                            <TableCell className="colunaEsquerda">
                                Memória
                            </TableCell>
                            <TableCell>
                                {produto.memory}
                            </TableCell>
                        </TableRow>
                        <TableRow>
                            <TableCell className="colunaEsquerda">
                                Marca
                            </TableCell>
                            <TableCell>
                                {produto.brand}
                            </TableCell>
                        </TableRow>
                        <TableRow>
                            <TableCell className="colunaEsquerda">
                                Chip
                            </TableCell>
                            <TableCell>
                                {produto.chipType}
                            </TableCell>
                        </TableRow>
                        <TableRow>
                            <TableCell className="colunaEsquerda">
                                Quantidade
                            </TableCell>
                            <TableCell>
                                {produto.quantity}
                            </TableCell>
                        </TableRow>
                    </TableBody>
                </Table>
            </Paper>
        </>
    )
}
