import React, { useEffect, useState } from 'react';
import api from '../../services/api';
import './styles.css';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';

export default function Produtos({ history }) {
    const [produto, setProduto] = useState([]);

    useEffect(() => {
        async function carregarProduto() {
            const id = localStorage.getItem('produto_id');
            const response = await api.get(`/products/${id}`)
            response.data.price = `R$ ${response.data.price.toLocaleString('pt-BR')}`
            console.log(response.data)
            setProduto(response.data)
        }

        carregarProduto();
    }, []);

    return (
        <>
            <div className="linhaUm">
                <header style={{backgroundImage: `url(${produto.picture})`}} />
                <h2>{produto.title}</h2>
            </div>
            <Paper>
                <Table>
                    <TableBody>
                        <TableRow>
                            <TableCell>
                                Valor
                            </TableCell>
                            <TableCell>
                                {produto.price}
                            </TableCell>

                            <TableCell>
                                Descrição
                            </TableCell>
                            <TableCell>
                                {produto.description}
                            </TableCell>

                            <TableCell>
                                Memório
                            </TableCell>
                            <TableCell>
                                {produto.memory}
                            </TableCell>

                            <TableCell>
                                Marca
                            </TableCell>
                            <TableCell>
                                {produto.brand}
                            </TableCell>

                            <TableCell>
                                Chip
                            </TableCell>
                            <TableCell>
                                {produto.chipType}
                            </TableCell>

                            <TableCell>
                                Quantidade chips
                            </TableCell>
                            <TableCell>
                                {produto.quantity}
                            </TableCell>
                        </TableRow>
                    </TableBody>
                </Table>
            </Paper>
        </>
    )
}
