import React, { useEffect, useState } from 'react';
//import api from '../../services/api';
import './styles.css';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';

export default function Produtos({ history }) {
    let [qtdProduto, setQtdProduto] = useState('');
    const produto_id = localStorage.getItem('produto_id');
    let array = localStorage.getItem('arrayProdutos');
    array = JSON.parse(array);
    const produtoFinal = array.forEach(function(p) { 
        if (p.id === produto_id) {
            produtoFinal = p
        }
     })
    
    /* const [produto, setProduto] = useState([qtdProduto]);

    useEffect(() => {
        async function carregarProduto() {
            setProduto(produtoFinal)
        }

        carregarProduto();
    }, [qtdProduto]); */

    function handleRemove(id) {
        /*localStorage.setItem('qtdTotal', qtdTotal--);
        document.getElementById('idQtd').innerHTML = qtdTotal;*/
    };

    function handleAdd(produto) {
        array.forEach(function(p) {
            if (p.id === produto.id) {
                if (produto.qtd < produto.quantity) {
                    produto.qtd++
                    p.qtd++
                    setQtdProduto(produto.qtd)
                    localStorage.setItem('produto', JSON.stringify(produto));
                    localStorage.setItem('arrayProdutos', JSON.stringify(array));
                }
            }
        })
        console.log(array)
        /*localStorage.setItem('produto', produto.qtd++);
        document.getElementById('idQtd').innerHTML = qtdTotal;*/
    };

    return (
        <>
            <div className="linhaUm">
                <header style={{backgroundImage: `url(${produtoFinal.picture})`}} />
                <div>
                    <h2>{produtoFinal.title}</h2>
                    <div className="divBtns">
                        <button className="btnRemover" onClick={() => handleRemove(produtoFinal.id)}>-</button>
                        <input type="text" value={produtoFinal.qtd} readOnly />
                        <button className="btnAdicionar" onClick={() => handleAdd(produtoFinal)}>+</button>
                    </div>
                </div>
            </div>
            <Paper>
                <Table>
                    <TableBody>
                        <TableRow>
                            <TableCell className="colunaEsquerda">
                                Valor
                            </TableCell>
                            <TableCell>
                                {produto.price}
                            </TableCell>
                        </TableRow>
                        <TableRow>
                            <TableCell className="colunaEsquerda">
                                Descrição
                            </TableCell>
                            <TableCell>
                                {produto.description}
                            </TableCell>
                        </TableRow>
                        <TableRow>
                            <TableCell className="colunaEsquerda">
                                Memória
                            </TableCell>
                            <TableCell>
                                {produto.memory}
                            </TableCell>
                        </TableRow>
                        <TableRow>
                            <TableCell className="colunaEsquerda">
                                Marca
                            </TableCell>
                            <TableCell>
                                {produto.brand}
                            </TableCell>
                        </TableRow>
                        <TableRow>
                            <TableCell className="colunaEsquerda">
                                Chip
                            </TableCell>
                            <TableCell>
                                {produto.chipType}
                            </TableCell>
                        </TableRow>
                    </TableBody>
                </Table>
            </Paper>
        </>
    )
}
