import React, { useEffect, useState } from 'react';
import api from './services/api';
import { BrowserRouter as Router, Link } from "react-router-dom";
import Routes from './routes';
import './App.css';
import carrinho from './assets/carrinho.svg';

function App() {

    let qtdTotal = 0
    localStorage.setItem('qtdTotal', qtdTotal);
    let [produtos, setProdutos] = useState([]);

    useEffect(() => {
        async function carregarProdutos() {
            const response = await api.get('/products')
            response.data.map(p =>( (p.qtd = p.qtd ? p.qtd : 0), (p.price = `R$ ${p.price.toLocaleString('pt-BR')}`) ))
            setProdutos(response.data)
            localStorage.setItem('arrayProdutos', JSON.stringify(response.data));
        }

        carregarProdutos();
    }, []);

    return (
        <>
            <div className="menu">
                Smartphones
                <Router>
                    <Link>
                        <div to="/carrinho">
                            <img src={carrinho} alt="Carrinho de compras" />
                            <label id="idQtd">{qtdTotal ? qtdTotal : 0}</label>
                        </div>
                    </Link>
                </Router>
            </div>
            <div className="container">
                <div className="content">
                    <Routes />
                </div>
            </div>
        </>
    );
}

export default App;
